sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {


    if (isLoading || newValue === '' || newValue == oldValue) {
        return;
    }


    var getUsr = new GlideAjax('GetUserDetails');

    getUsr.addParam('sysparm_name','getDetails');

    getUsr.addParam('sysparm_userID', g_form.getValue('u_name'));

    getUsr.getXML(populateUserDetails);



    function populateUserDetails(response){

        var answers = response.responseXML.documentElement.getAttribute("answer");
        var UsrDetail = answers.split(',');

        g_form.setValue('u_email',UsrDetail[0]);

        g_form.setValue('u_first_name',UsrDetail[1]);

        g_form.setValue('u_last_name',UsrDetail[2]);


g_form.setValue('u_user_id',UsrDetail[3]);


g_form.setValue('u_department',UsrDetail[4]);


g_form.setValue('u_manager',UsrDetail[5]);


    }
}
