sys_script_include.script:
var GetUserDetails = Class.create();
// Extend the global.AbstractAjaxProcessor class
GetUserDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{

    getDetails: function() {
		var retVal; 
        var userRecord = new GlideRecord("sys_user");
        userRecord.get(this.getParameter('sysparm_userID'));
       retVal = userRecord.email +','+userRecord.first_name+','+userRecord.last_name+','+userRecord.user_name+','+userRecord.department.name+','+userRecord.manager.name;
		return retVal;
    },
    type: 'GetUserDetails'
});
